import React, { Component } from "react"

class App extends Component {
    state = {
        numbers: {
            diceOne: 0,
            diceTwo: 0,
            diceThree: 0,
            diceFour: 0,
            diceFive: 0,
            diceSix: 0
        }
        // dice: {
        //     diceOne: {
        //         number: random(),
        //         isEdit: true
        //     },
        //     diceTwo: {
        //         number: random(),
        //         isEdit: true
        //     }
        // }
    }

    

     //function: if statement

    random = () => Math.floor(Math.random() * 6) + 1

    rollDice = () => this.setState({
        numbers: {
            diceOne: this.random(),
            diceTwo: this.random(),
            diceThree: this.random(),
            diceFour: this.random(),
            diceFive: this.random(),
            diceSix: this.random()
        }
    })


    render() {
        // const dice = state.numbers.map((number, index) => <DiceBox key={index} number={number} />)
        return (
            <div>
                <div className="box">
                    <p>{this.state.numbers.diceOne}</p>
                    <p>{this.state.numbers.diceTwo}</p>
                    <p>{this.state.numbers.diceThree}</p>
                    <p>{this.state.numbers.diceFour}</p>
                    <p>{this.state.numbers.diceFive}</p>
                    <p>{this.state.numbers.diceSix}</p>
                </div>
                <button onClick={this.rollDice}>press</button>

            </div>
        )
    }
}
export default App

