                diceTwo:0,
                diceThree:0,
                diceFour:0,
                diceFive:0,
                diceSix:0
            }
        }

    random = () => Math.floor(Math.random() * 6) + 1
    
    rollDice = () => this.setState(prevState => ({numbers:{
        diceOne}}))
  

        render() {
            // const dice = this.state.numbers.map((number, index) => <DiceBox key={index} number={number} />)
            return (
                <div>
                {/* {dice} */}
                <button onClick={this.rollDice}>press</button>
