import React, { Component } from "react"
import DiceBox from "./DiceBox"

class App extends Component {
    constructor() {
        super()
        this.state = {
            numbers: [1, 2, 3, 4, 5, 6]

        }
    }
    
    randomNum = () => this.setState(prevState => ({numbers: Math.floor(Math.random * prevState.numbers)})) 

        render() {
            const dice = this.state.numbers.map(number => <DiceBox  number={number} />)
            return (
                <div>
                {dice}
                <button onClick={this.randomNum}>press</button>
            </div>
        )
    }
}
console.log(this.randomNum())
export default App
