import React, { Component } from "react"
import DiceBox from "./DiceBox"

class App extends Component {
    constructor() {
        super()
        this.state = {
            numbers: [1, 2, 3, 4, 5, 6]
        }
        this.randomNumber = this.randomNumber.bind(this)
    }

    random = () => this.setState()
    
    rollDice(){
        this.setState(prevState => {
            return {
                numbers: Math.floor(Math.random() * prevState.numbers)
            }
        })
    }
    // randomNum = () => this.setState(prevState => ({Math.floor(Math.random * prevState.numbers)} )) 

        render() {
            const dice = this.state.numbers.map((number, index) => <DiceBox key={index} number={number} />)
            return (
                <div>
                {dice}
                <button onClick={this.rollDice}>press</button>
            </div>
        )
    }
}
export default App
