import React, { Component } from "react"
import Dice from "./Dice"

class App extends Component {
    state = {
        numbers: {
            diceOne: 0,
            diceTwo: 0,
            diceThree: 0,
            diceFour: 0,
            diceFive: 0,
            diceSix: 0
        }
    }

    random = () => Math.floor(Math.random() * 6) + 1

    rollDice = () => this.setState({
        numbers: {
            diceOne: this.random(),
            diceTwo: this.random(),
            diceThree: this.random(),
            diceFour: this.random(),
            diceFive: this.random(),
            diceSix: this.random()
        }
    })


    render() {
        const diceNum = this.state.map(arr => <Dice key={index} dice={arr} roll={this.rollDice}/>)
