        super()
        this.state = {
            numbers: [1, 2, 3, 4, 5, 6]

        }
    }
    
    
    // randomNum = () => this.setState(prevState => ({Math.floor(Math.random * prevState.numbers)} )) 

        render() {
            const dice = this.state.numbers.map(number => <DiceBox number={number} />)
            return (
                <div>
                {dice}
                <button onClick={this.randomNum}>press</button>
            </div>
        )
    }
}
export default App
